import React, { useState } from "react";

import "./Expense.css";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import Card from "../UI/Card";

const Expense = (props) => {
  // const expenses = props.items;

  const [filteredYear, setFilteredYear] = useState("2020");

  const filterHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };

  const filteredExpenses = props.items.filter(
    (single) => single.date.getFullYear().toString() === filteredYear
  );

  const expensesContent = <p>No expenses found!</p>

  if( filteredExpenses.length > 0) { expensesContent =
    filteredExpenses.map((expense) => (
      <ExpenseItem
        key={expense.id}
        title={expense.title}
        amount={expense.amount}
        date={expense.date}
      />
    ))
  };

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter defaultYear={filteredYear} onFilter={filterHandler} />
        {filteredExpenses.length === 0 && "No expenses found!"}
        {filteredExpenses.length > 0 &&
          filteredExpenses.map((expense) => (
            <ExpenseItem
              key={expense.id}
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
            />
          ))}
      </Card>
    </div>
  );
};

export default Expense;
